vec2 = {
	meta = {
		__add = function (s, v) 
			return vec2.new(s.x + v.x, s.y + v.y)
		end
	},

	new = function(x, y)
		local v = {
			x = x or 0,
			y = y or 0,
		}
		
		setmetatable(v, vec2.meta)
	
		return v
	end
}



player = {
	SPEED = 2,
	MAX_ACCEL = 10,

	pos = vec2.new(),
	
	move = function(s, v)
		s.pos = s.pos + v
	end
}


function TIC()
	cls()
	player:move(get_arrows())

	print(player.pos.x,84,84)
end


function get_arrows()
	return vec2.new(
		(btn(3) and 1 or 0)-(btn(2) and 1 or 0),
		(btn(0) and 1 or 0)-(btn(1) and 1 or 0)
	)
end

